[statement]
  [item] Conduct backups of user-level information contained in {{ insert: param, cp-09_odp.01 }} {{ insert: param, cp-09_odp.02 }};
  [item] Conduct backups of system-level information contained in the system {{ insert: param, cp-09_odp.03 }};
  [item] Conduct backups of system documentation, including security- and privacy-related documentation {{ insert: param, cp-09_odp.04 }} ; and
